import React from 'react';
import { Link } from "react-router-dom";
import {
  AppBar,
  Toolbar,
  IconButton,
  List,
  ListItem,
  Container,
} from "@material-ui/core";
import {Home} from '@material-ui/icons'
import { makeStyles } from "@material-ui/core/styles";
import InputBase from "@material-ui/core/InputBase";
import Divider from "@material-ui/core/Divider";
import SearchIcon from "@material-ui/icons/Search";
import DirectionsIcon from "@material-ui/icons/Directions";


const MyStyle = makeStyles((theme)=>({
  navDisplayFlex: {
    display: `flex`,
    "flex-direction": "row",
    justifyContent: `space-between`,
  },
  linkText: {
    textDecoration: `none`,
    textTransform: `uppercase`,
    color: `white`,
  },
  root: {
    padding: "2px 4px",
    display: "flex",
    alignItems: "center",
    width: 400,
  },
  input: {
    marginLeft: theme.spacing(1),
    flex: 1,
  },
  iconButton: {
    padding: 10,
  },
  divider: {
    height: 28,
    margin: 4,
  },
}));




export default function navBar() {
    const navLinks = [
      { title: "Electronic Devices", path: "/electronicDevices" },
      { title: "Groceries", path: "/groceries" },
      {title : 'Fashions', path : '/fashions'},
      {title : 'Contact Us', path :'/contactus'},
      {title : 'Login / Signup' , path : '/loginSignup'},
    ];


  const classes = MyStyle();


    return (
      <div>
        <AppBar position="static">
          <Toolbar>
            <Container className={classes.navDisplayFlex}>
              <IconButton edge="start" color="inherit" aria-label="home">
                <Home fontSize="large" />
              </IconButton>

              <List component="nav" className={classes.navDisplayFlex}>
                {navLinks.map(({ title, path }) => (
                  <ListItem button key={title}>
                    <Link to={path} className={classes.linkText}>
                      {title}
                    </Link>
                  </ListItem>
                ))}
              </List>
            </Container>
          </Toolbar>
        </AppBar>

        <Paper className={classes.root} maxWidth="xs">
          <InputBase
            className={classes.input}
            placeholder="Search Google Maps"
            inputProps={{ "aria-label": "search on my store" }}
          />
          <IconButton
            type="submit"
            className={classes.iconButton}
            aria-label="search"
          >
            <SearchIcon />
          </IconButton>
        </Paper>
      </div>
    );
}
